DROP TABLE IF EXISTS company;
DROP TABLE if exists talent;

create table company (
                         id bigint generated by default as identity,
                         company_info varchar(255),
                         identity_color varchar(255) not null,
                         image varchar(255) not null,
                         name varchar(255) not null,
                         permission varchar(255) not null check (permission in ('APPROVED','PENDING','DENIED')),
                         updated_at timestamp(6),
                         primary key (id)
);

create table talent (
                        id bigint generated by default as identity,
                        base_url varchar(255),
                        description varchar(255),
                        icon varchar(255),
                        keyword varchar(255) not null,
                        permission varchar(255) check (permission in ('APPROVED','PENDING','DENIED')),
                        company_id bigint,
                        primary key (id)
);





